    const { id, nombre, src, precio, stock } = product;


    return (
        <div className='col-sm-6 col-md-4 mb-4'>
            <h4>{nombre}</h4>
            <h5>${precio}</h5>
            <h5>{stock} u.</h5>
            {/* addToCart de que producto viene??? hay que configurar  */}
